StructuredTextModel.Text:
(*This case statement covers the spin right feature of the iCart, where the iCart will... 
> Stop iCart Drives "SS_BLV_stp"  and give permission to "spinstateactive" to deactivate Tracklost and tape track features. 
> Set "PrevPos" = 0; and AZD set steering position to current pos. 
> Set AZD steering pos to right steering pos. 
> Wait until AZD is in pos. 
> when in pos, un-stop and set rear drives to spin on spot and set speed > Spin until Sensor Count for tape tracking is >= 4 
> When tape is found, stop iCart and recenter azd to "Steering_home" to center drive 
> Set Spinstateactive to false to give tape control back to do tracking*)


CASE SpinstateL OF
	
	SL_idle:
	
		TagDriveSetL 	:= FALSE;
		
			IF Spinleft = TRUE THEN
				
				SpinStateComplete := FALSE;
			 
			SpinstateL := SL_WaitToStop;
			
			END_IF;

	SL_WaitToStop:
	
		SpinPause		:= TRUE;
		SS_BLV_Stp		:= TRUE;
		SpinStateActive := TRUE;
		PrevSetPos		:= 0;
		AZD_SetPos 		:= AZD_ActPos;
		
			IF AGVStopped and (LeftSpeedOut = 0) AND (RightSpeedOut = 0) THEN
				
				PrevSetPos := 0;
				
				SpinstateL := SL_AZDSetL;
			
			ELSIF TimeInState > 2000 (*4s*) THEN
			
			SpinstateL := SL_HandleFault;	
				
			END_IF;
	
	
	SL_AZDSetL:

			AZD_SetPos := Steering_LeftPos;
		
			SpinstateL := SL_WaitForAZDSet;
			
			
		
	SL_WaitForAZDSet:
	
		
	
		IF (AZD_ActPos > 89000) THEN
			
			SpinstateL := SL_SpinL;
		
		ELSIF TimeInState > 1000 (*2s*) THEN
			
			SpinstateL := SL_HandleFault;	
			
		END_IF;
		
	SL_SpinL:
	
		SpinPause 		:= FALSE;
		TagDriveSetL	:= TRUE;
		SpinSetSpeed 	:= TRUE;
		DriveSpin 		:= TRUE;
		SS_BLV_Stp		:= FALSE;
		
		SpinstateL 		:= SL_WaitForTape;
		
		IF TimeInState > 6000 (*12s*) THEN
			
			SpinstateL := SL_HandleFault;
		END_IF;
		
		
	SL_WaitForTape:
	
		IF G_SensorCount >= 4 THEN
			
			SpinstateL := SL_TapeFound;
			
		END_IF;	
	
// Completion of Tag Request Spin

	SL_TapeFound:
	
		SpinPause 		:= 	TRUE;
		SpinSetSpeed	:=  FALSE; // disable spin speed. 
		TagDriveSetL	:=  FALSE;
		SpinStateActive	:=	TRUE;
		
		SpinstateL 		:= SL_AZDCenter;
		
	
	SL_AZDCenter:
	
		PrevSetPos 		:= 0;
		
		AZD_SetPos 		:= Steering_Home;
		
		IF (AZD_ActPos <= 56700) THEN
		
			SpinStateActive := FALSE;
		
			SpinstateL 		:= SL_AZDWaitToCenter;
			
		ELSIF TimeInState > 2000 (*4s*) THEN
			
			SpinstateL := SL_HandleFault;
			
		END_IF;
		
		
	SL_AZDWaitToCenter:
	
		IF (AZD_ActPos <= 56500) THEN
			
			SpinStateComplete := TRUE;
			
			SpinstateL := SL_Complete;
			
		END_IF;	
	
	
	SL_Complete:
	
		SpinPause 			:= FALSE;
		Spinleft			:= FALSE;
		TagSpin				:= FALSE;
		SpinStateActive 	:= FALSE;
		SpinSetSpeed    	:= FALSE;
		DriveSpin       	:= FALSE;
		SpinStateComplete	:= TRUE;
		
		SpinstateL := SL_idle; // return to idle state when complete.
		
	SL_HandleFault:
	
		SpinPause 			:= FALSE;
		TagDriveSetL 		:= FALSE;
		Spinleft			:= FALSE;
		TagSpin 			:= FALSE;
		SpinStateActive 	:= FALSE;
		SpinSetSpeed    	:= FALSE;
		DriveSpin       	:= FALSE;
		
		SpinstateL	:= 	SL_idle;
	
	SL_Reset:				//When Reset state, Turn all Variables off and return to idle state. 
	
	IF SpinStateReset THEN
		
		SpinPause 			:= FALSE;
		TagDriveSetL 		:= FALSE;
		Spinleft			:= FALSE;
		TagSpin 			:= FALSE;
		SpinStateActive 	:= FALSE;
		SpinSetSpeed    	:= FALSE;
		DriveSpin       	:= FALSE;
	
		
		SpinstateL := SL_idle;
		
	END_IF;
END_CASE; 

////////////////////////////////////////////////////////////

IF SpinstateL <> Prev_Spinstate OR SpinstateL = SL_idle THEN
	TimeInState := 0;
	
ELSE
	Inc(TimeInState);
	
END_IF;

Prev_Spinstate := SpinstateL;
