StructuredTextModel.Text:
(*This case statement covers the spin right feature of the iCart, where the iCart will... 
> Stop iCart Drives "SS_BLV_stp"  and give permission to "spinstateactive" to deactivate Tracklost and tape track features. 
> Set "PrevPos" = 0; and AZD set steering position to current pos. 
> Set AZD steering pos to right steering pos. 
> Wait until AZD is in pos. 
> when in pos, un-stop and set rear drives to spin on spot and set speed > Spin until Sensor Count for tape tracking is >= 4 
> When tape is found, stop iCart and recenter azd to "Steering_home" to center drive 
> Set Spinstateactive to false to give tape control back to do tracking*)

////////////////////////////
//Case for Spin state Right.
////////////////////////////

CASE SpinstateR OF
	
	SS_idle:
	
			TagDriveSetR	:=  FALSE; //Set Drives to false to not spin on spot and give way to normal driving. 
			
			
			IF Spinright THEN
				
				SpinStateComplete 	:= FALSE;	//deactivate AGV running set. 
			
				SpinstateR 			:= SS_WaitToStop; //move to next state.
		
			END_IF;
			

	SS_WaitToStop:
	
		SpinPause		:= TRUE; 		//Activates PauseAGV.
		SS_BLV_Stp		:= TRUE;		//Sets BLV_Stop true to stop Rear Drives.
		SS_BLV_MBFREE	:= TRUE;		//Set MBFREE True.
		SpinStateActive := TRUE;		//Removes tape tracking control and allows free setting for the AZD Drive. 
		PrevSetPos		:= 0;			//Set Previous pos to 0 to set a new position. 
		AZD_SetPos 		:= AZD_ActPos;	//Set current pos to new pos to keep drive central. 
		
		
			IF AGVStopped AND (LeftSpeedOut = 0) AND (RightSpeedOut = 0) THEN
				
				PrevSetPos 		:= 0;				//Set Previous pos to 0 to set a new position. 
				AZD_SetPos		:= Steering_Home;	//set pos to home.
				
				SpinstateR 	:= SS_AZDSetR;
			
			ELSIF TimeInState > 2000 THEN
				
				SpinstateR := SS_HandleFault;
				
			END_IF;
	
		//
	SS_AZDSetR:
		
			SS_BLV_Stp	:= TRUE;
			PrevSetPos 	:= 0;					//Set Previous pos to 0 to set a new position. 
			AZD_SetPos 	:= Steering_Rightpos;	//Set Current pos to go to right steering pos.
			
			SpinstateR 	:= SS_WaitforAZDSet;
			
		
	SS_WaitforAZDSet:
	

		
		IF (AZD_ActPos <= 21500) THEN
			
			SpinstateR := SS_SpinR;
			
		ELSIF SpinStateReset THEN
			
			SpinStateR := SS_Reset;
			
		END_IF;
		
		
	SS_SpinR:
		
		SS_BLV_Stp		:= FALSE;	//Set BLV_stop to false.
		SS_BLV_MBFREE	:= FALSE;	//Set MBFREE. 
		SpinPause 		:= FALSE;	//set pause to false. 
		TagDriveSetR 	:= TRUE;	//set drives to spin. 
		SpinSetSpeed 	:= TRUE;	//Set SPin speed value to overwirte speed. 
		DriveSpin 		:= TRUE;	//Activate Drive Power. 
		
		SpinstateR 		:= SS_WaitforTape;
		
		
	SS_WaitforTape:
	
		IF G_SensorCount >= 4 THEN			//IF tape sensor see's more than 4...
			
			SpinstateR := SS_TapeDetect;	//Move to next state. 
			
		ELSIF SpinStateReset THEN			// IF reset function is activated then...
			SpinStateR := SS_Reset;			//Move to reset state. 
			
		END_IF;	


	SS_TapeDetect:
		
		SS_BLV_Stp		:= 	TRUE;	//SET blv_Stop variable true and stop rear drives. 
		SS_BLV_MBFREE	:=	TRUE;	//Set MBFREE.
		SpinPause 		:= 	TRUE; 	//stop AGV
		SpinSetSpeed	:=  FALSE;  //disable spin speed. 
		TagDriveSetR	:=  FALSE;  //Disable Fwd and Rev drive config. 
		SpinStateActive	:=	TRUE;	//Give steering Control to State. 
		
		SpinstateR 		:= SS_AZDCenter;	// Move to center drive once tape found. 
	
	
	SS_AZDCenter:
			
		PrevSetPos := 0; 					//set previous pos to 0;
		
		AZD_SetPos := Steering_Home;		//set current position back to home. 
		
		IF (AZD_ActPos >= 54500) THEN	
			
			SpinStateActive := FALSE;		//When value is met or exceeded, then give control back to tape guidance. 
			
			
			SpinstateR := SS_WaitforCenter;	//move to next state. 
	
		END_IF;
		
		
	SS_WaitforCenter:
	
		IF (AZD_ActPos >= 54700) THEN
			
			SpinStateComplete 	:= TRUE;		//Set true and set AGV running state to true. 
			SS_BLV_Stp			:= FALSE;		//Set BLV stop mode to false to release brakes. 
			SS_BLV_MBFREE		:= FALSE;
			
			SpinstateR := SS_Complete;			//move to next state. 
			
		END_IF;
	
	
	SS_Complete:
	
		SpinPause 			:= FALSE;			//Set all to false except spinstatecomplete and return to idle state. 
		Spinright			:= FALSE;
		TagSpin 			:= FALSE;
		SpinStateActive 	:= FALSE;
		SpinSetSpeed    	:= FALSE;
		DriveSpin       	:= FALSE;
		SpinStateComplete   := TRUE;
		
		SpinstateR := SS_idle; // return to idle state when complete.
	
	SS_HandleFault:
	
		SpinPause 			:= FALSE;
		TagDriveSetR 		:= FALSE;
		Spinright			:= FALSE;
		TagSpin 			:= FALSE;
		SpinStateActive 	:= FALSE;
		SpinSetSpeed    	:= FALSE;
		DriveSpin       	:= FALSE;
		SS_BLV_Stp			:= FALSE;
		SS_BLV_MBFREE		:= FALSE;
		
		SpinstateR := SS_idle;

			

	SS_Reset:				//When Reset state, Turn all Variables off and return to idle state. 
	
	IF SpinStateReset THEN
		
		SpinPause 			:= FALSE;
		TagDriveSetR 		:= FALSE;
		Spinright			:= FALSE;
		TagSpin 			:= FALSE;
		SpinStateActive 	:= FALSE;
		SpinSetSpeed    	:= FALSE;
		DriveSpin       	:= FALSE;
		SS_BLV_Stp			:= FALSE;
		SS_BLV_MBFREE		:= FALSE;
	
		
		SpinstateR := SS_idle;
		
	END_IF;
		
	
END_CASE; 

/////////////////////////////////////////////////////////////

IF SpinstateR <> Prev_Spinstate OR SpinstateR = SS_idle THEN
	TimeInState := 0;
	
ELSE
	Inc(TimeInState);
END_IF;

Prev_Spinstate := SpinstateR;
